    /**
     * @param char1 First character for comparison
     * @param char2 Second character for comparison
     * @return 0 if both characters are same, 1 if both characters
     * are similar but from different language and -1 if any
     * one or both characters are not found
     */
    public static int charCompare(char char1, char char2) {
        if (char1 == char2) {
            return 0;
        }
        int charIndex1 = -1;
        int charIndex2 = -1;

        String charLang1 = getLanguage(char1);
        String charLang2 = getLanguage(char2);

        if (charLang1 != null && charLang2 != null) {

            if (charLang1.equals("ISO15919") || charLang1.equals("IPA")) {
                charIndex1 = CharacterMap.charmapTransphon.get(charLang1).indexOf(char1 + "");
            }

            if (charLang2.equals("ISO15919") || charLang2.equals("IPA")) {
                charIndex2 = CharacterMap.charmapTransphon.get(charLang2).indexOf(char2 + "");
            }

            if (charIndex1 == -1) {
                charIndex1 = CharacterMap.charmap.get(charLang1).indexOf(char1);
            }
            if (charIndex2 == -1) {
                charIndex2 = CharacterMap.charmap.get(charLang2).indexOf(char2);
            }
            if (charIndex1 == charIndex2) {
                return 1;
            }
        }
        return -1;
    }

    /**
     * Get the language of given `char'
     * Return the language of given character, if character language
     * is not found `None' is returned.
     *
     * @param ch The char whose language is to be detected
     * @return string representing language or None if char not found
     * in our mapping.
     */
    public static String getLanguage(char ch) {
        for (Map.Entry<String, List<Character>> entry : CharacterMap.charmap.entrySet()) {
            String lang = entry.getKey();
            if (CharacterMap.charmap.get(lang).contains(Character.toLowerCase(ch))) {
                return lang;
            }
        }

        for (Map.Entry<String, List<String>> entry : CharacterMap.charmapTransphon.entrySet()) {
            String lang = entry.getKey();
            if (CharacterMap.charmapTransphon.get(lang).contains(ch + "")) {
                return lang;
            }
        }
        return null;
    }
